DROP TABLE IF EXISTS `ass_assets_tag`;
CREATE TABLE `ass_assets_tag` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `width` float DEFAULT NULL COMMENT '标签宽度，单位:px',
  `height` float DEFAULT NULL COMMENT '标签高度，单位:px',
  `content` longtext COLLATE utf8_bin COMMENT '资产标签打印HTML内容',
  `assets_sql` varchar(500) COLLATE utf8_bin DEFAULT NULL COMMENT '资产模板数据sql',
  `type` int(1) DEFAULT NULL COMMENT '标签类型(1=标准，2=自定义)',
  `enable` bit(1) DEFAULT NULL COMMENT '启用状态(0=禁用，1=启用)',
  `del_flag` bit(1) DEFAULT NULL COMMENT '删除标记(0=未删除，1=已删除)',
  `create_time` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `remark` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '备注说明',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='资产打印标签模板数据表';

